import React, { useState } from "react";
import { Container, Col, Row, Button } from "react-bootstrap";
import SearchInfo from "../SearchInfo.jsx";
const { DFS, BFS } = require("../../Algorithms/Algorithms.js");

const BreadthFirstSearch = ({ Algorithm }) => {
	const [play, setPlay] = useState(false);
	const [origin, setOrigin] = useState("0");
	const [destination, setDestination] = useState("99");
	const [obstacles, setObstacles] = useState([
		95, 66, 12, 8, 47, 85, 77, 87, 97, 300, 986, 445, 692, 776, 290, 887,
		999, 998, 500
	]);
	const [tree, setTree] = useState(() => {
		let t = [];
		let obstacles = [
			95, 66, 12, 8, 47, 85, 77, 87, 97, 300, 986, 445, 692, 776, 290,
			887, 999, 998, 500
		];

		for (let i = 0; i < 10000; i++) {
			t.push([]);
			if (obstacles.indexOf(i) === -1) {
				if (i % 100 !== 0 && obstacles.indexOf(i - 1) === -1)
					t[i].push(String(i - 1));
				if (i + 100 < 10000 && obstacles.indexOf(i + 100) === -1)
					t[i].push(String(i + 100));
				if (
					i + 1 < Math.floor(i / 100) * 100 + 1 &&
					obstacles.indexOf(i + 1) === -1
				)
					t[i].push(String(i + 1));
				if (i - 100 >= 0 && obstacles.indexOf(i - 100) === -1)
					t[i].push(String(i - 100));
			}
		}
		return t;
	});

	const graph = () => {
		let className = "";
		alert(JSON.stringify(DFS(tree, origin, destination)));
		let g = [];
		for (let i = 0; i < 10000; i++) {
			let traverseStyle = {};
			let indexInVisited = DFS(tree, origin, destination).checked.indexOf(
				String(i)
			);
			if (String(i) === destination) {
				className = "destination";
			} else if (String(i) === origin) {
				className = "origin";
			} else if (indexInVisited !== -1) {
				className = "visited";
				traverseStyle = {
					animation: `flip 0.5s ${indexInVisited / 5}s,
	 toVisited 0.5s ${indexInVisited / 5}s linear forwards`
				};
			} else if (obstacles.indexOf(i) !== -1) {
				className = "obstacle";
			} else {
				className = "normal";
			}
			g.push(
				<div id={i} className={className} style={traverseStyle}></div>
			);
		}

		return g;
	};

	return (
		<Container fluid className="text-center">
			<Row className="mt-3 mb-5">
				<h1>Breadth First Search</h1>
			</Row>
			<Row className="d-flex justify-content-center mb-2">
				<SearchInfo algorithm={Algorithm} />
			</Row>
			<Row className="d-flex flex-wrap">
				{" "}
				<div id="graph" className="mb-3">
					{graph()}
				</div>
			</Row>

			<Row className="d-flex justify-content-center">
				<Button className="bg-primary">
					{() => {
						return play ? "stop" : "play";
					}}
				</Button>
			</Row>
		</Container>
	);
};
export default BreadthFirstSearch;
