import React, { useState, useEffect } from "react";
import {
	Container,
	Row,
	Col,
	Form,
	FloatingLabel,
	Button
} from "react-bootstrap";

const Contact = () => {
	const [name, setName] = useState("");
	const [email, setEmail] = useState("");
	const [comment, setComment] = useState("");
	useEffect(() => {}, []);

	const handleSubmit = async e => {
		e.preventDefault();

		const feedback = { name, email, comment };
		const response = await fetch("http://localhost400/api/feedback", {
			method: "POST",
			body: JSON.stringify(feeeback),
			headers: {
				"Content-Type": "application/json"
			}
		});

		const res = response.json()

		if(!response.ok)
		{

		}else
		{

		}
	};
	return (
		<Container
			fluid
			className="justify-content-center bg-body-secondary h-100"
		>
			<Row>
				<Col>
					<h1 className="text-center text-light-emphasis mt-3">
						Contact
					</h1>
				</Col>
			</Row>

			<Form className="mt-5">
				<Row className="text-center justify-content-evenly">
					<Col sm>
						<Form.Group>
							<FloatingLabel
								controlId="floatingInput"
								label="Name"
								className="mb-3 mx-auto"
								style={{ maxWidth: "15rem" }}
							>
								<Form.Control
									type="text"
									placeholder="My Name/Organisation"
								/>
							</FloatingLabel>
						</Form.Group>
					</Col>
					<Col sm>
						<Form.Group>
							<FloatingLabel
								controlId="floatingInput"
								label="Email Adress"
								className="mx-auto"
								style={{ maxWidth: "15rem" }}
							>
								<Form.Control
									type="email"
									placeholder="iam@interested.com"
								/>
							</FloatingLabel>

							<Form.Text className="text-muted">
								I will never share your email with anyone
							</Form.Text>
						</Form.Group>
					</Col>
				</Row>
				<Row className="text-center justify-content-evenly mt-5">
					<Col>
						<Form.Group>
							<FloatingLabel
								controlId="floatingTextarea"
								label="Comments"
								className="mx-auto"
								style={{
									maxWidth: "30rem"
								}}
							>
								<Form.Control
									as="textarea"
									placeholder="Leave a comment here"
									style={{
										height: "8rem"
									}}
								/>
							</FloatingLabel>
						</Form.Group>
					</Col>
				</Row>
				<Row className="justify-content-center mt-5 pb-3">
					<Col id="subCol">
						<Form.Group>
							<Button id="contactSubmit">Submit</Button>
						</Form.Group>
					</Col>
				</Row>
			</Form>
		</Container>
	);
};

export default Contact;
