import React, { useState } from "react";
import { Container, Col, Row, Button } from "react-bootstrap";
import SearchInfo from "../SearchInfo.jsx";
const { BFS } = require("../../Algorithms/Algorithms.js");

const BreadthFirstSearch = ({ Algorithm }) => {
	const [play, setPlay] = useState(false);
	const [origin, setOrigin] = useState("0");
	const [tree, setTree] = useState(() => {
		let t = [];
		for (let i = 0; i < 100; i++) {
			t.push([]);
			if (i - 1 >= 0) t[i].push(String(i - 1));
			if (i + 1 < 10) t[i].push(String(i + 1));
			if (i + 10 < 100) t[i].push(String(i + 10));
			if (i - 10 >= 0) t[i].push(String(i - 10));
		}
		return t;
	});

	const [destination, setDestination] = useState("99");
	const fTm = () => {
		const v = setTimeout(() => {
			alert("hi");
		}, 5000);
		clearTimeout(v);
	};
	const graph = () => {
		function delayed() {
			let className = "";
			let g = [];
			for (let i = 0; i < 100; i++) {
				if (String(i) === destination) {
					className = "destination";
				} else if (String(i) === origin) {
					className = "origin";
				} else if (
					BFS(tree, origin, destination).checked.indexOf(
						String(i)
					) !== -1
				) {
					fTm();
					className = "visited";
				} else {
					className = "bg-light";
				}
				g.push(
					<div id={i} className={className}>
						{i}
					</div>
				);
			}

			return g;
		}
	};

	return (
		<Container fluid className="text-center">
			<Row className="mt-3 mb-5">
				<h1>Breadth First Search</h1>
			</Row>
			<Row className="d-flex justify-content-center mb-2">
				<SearchInfo algorithm={Algorithm} />
			</Row>
			<div id="graph" className="mb-3">
				{graph()}
			</div>
			<Row className="d-flex justify-content-center">
				<Button className="bg-primary">
					{() => {
						return play ? "stop" : "play";
					}}
				</Button>
			</Row>
		</Container>
	);
};
export default BreadthFirstSearch;
