import React, { useState, useEffect } from "react";

import Chessboard from "chessboardjsx";

import "./TTT.css";
import "./../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./../node_modules/bootstrap/dist/js/bootstrap.min.js";

import { Container, Row, Col, Button } from "react-bootstrap";
import MainNav from "./Components/MainNav.jsx";
//App
const App = () => {



	return (
		<Container
			fluid
			className="min-vh-100 min-vw-100 bg-secondary-subtle overflow-x-hidden p-0"
			data-bs-theme="dark"
		>
			<MainNav toggleMode={toggleMode} />
			<Row
				className="d-flex text-center mt-3"
				styles={{ color: "rgba(75,87,89,0.846)" }}
			>
				<h1>{mode}</h1>
			</Row>
			<Container className="m-0 p-4 h-100">
				<Chessboard
					position={fen}
					orientation={turn}
					width="300"
					boardStyle={{ boxShadow: "0 0 .5rem black", padding: "0" }}
					darkSquareStyle={{ backgroundColor: "#11dfef" }}
					lightSquareStyle={{
						backgroundColor: "rgba(255,255,255,0.17)"
					}}
					dropSquareStyle={{
						backgroundColor: "rgba(0,0,0,0.119)",
						boxShadow: "0 0 .75rem rgb(1,187,197)"
					}}
					onSquareClick={square => {
						setLegalStyles(getLegalMoves(square));
						setActiveSquare(square);
						playMove(square);
					}}
					squareStyles={legalStyles}
					onMouseOverSquare={square => {
						setLegalStyles(getLegalMoves(square));
					}}
					allowDrag={() => {
						return false;
					}}
				/>
			</Container>
			<Row
				className="d-flex text-center mt-3 justify-content-center"
				styles={{ color: "rgba(75,87,89,0.846)" }}
			>
				<Button
					id="btnReset"
					onClick={() => {
						chess.reset();
						setTurn("white");
						setFen(
							"rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1"
						);
					}}
				>
					Reset
				</Button>
			</Row>
		</Container>
	);
};

export default App;
