import React, { useState, useEffect } from "react";
import { Container, Col, Row, Button } from "react-bootstrap";
import SearchInfo from "./SearchInfo.jsx";
const { BFS, DFS, Djk, Astar } = require("../Algorithms/Algorithms.js");

const SearchView = ({ Algorithm }) => {
	const [play, setPlay] = useState(false);
	const [origin, setOrigin] = useState("0");
	const [destination, setDestination] = useState("296");

	const [traversed, setTraversed] = useState([]);
	const [obstacles, setObstacles] = useState([]);
	useEffect(() => {
		let obs = [];
		for (let i = 0; i < 100; i++) {
			obs.push(Math.floor(Math.random() * 400));
		}

		let t = [];
		for (let i = 0; i < 400; i++) {
			if (obs.indexOf(i) === -1) {
				t.push([]);
				if (i % 20 !== 0 && obs.indexOf(i - 1) === -1)
					t[t.length-1].push(String(i - 1));
				if (i + 20 < 400 && obs.indexOf(i + 20) === -1)
					t[i].push(String(i + 20));
				if ((i - 19) % 20 !== 0 && obs.indexOf(i + 1) === -1)
					t[i].push(String(i + 1));
				if (i - 20 >= 0 && obs.indexOf(i - 20) === -1)
					t[i].push(String(i - 20));
			}
		}
		setObstacles(obs);
		switch (Algorithm.name) {
			case "Breadth First Search":
				setTraversed(BFS(t, origin, destination).checked);
				break;
			case "Depth First Search":
				setTraversed(DFS(t, origin, destination).checked);
				break;
			case "Dijkstra's Algorithm":
				setTraversed(Djk(t, origin, destination));
				break;
			case "A*":
				setTraversed(Astar(t, origin, destination));
				break;
		}
	}, [origin, destination, Algorithm]);

	const graph = () => {
		let className = "";

		let g = [];
		for (let i = 0; i < 400; i++) {
			let indexInVisited = traversed.indexOf(String(i));
			if (String(i) === destination) {
				className = "destination";
			} else if (String(i) === origin) {
				className = "origin";
			} else if (obstacles.indexOf(i) !== -1) {
				className = "obstacle";
			} else {
				className = "normal";
			}
			g.push(<div id={i} className={className}></div>);
		}
		alert("done");
		return g;
	};

	return (
		<Container fluid className="text-center">
			<Row className="mt-3 mb-5">
				<h1>{Algorithm.name}</h1>
			</Row>
			<Row className="d-flex justify-content-center mb-2">
				<SearchInfo algorithm={Algorithm} />
			</Row>
			<Row className="d-flex flex-wrap">
				{" "}
				<div id="graph" className="mb-3">
					{graph()}
				</div>
			</Row>

			<Row className="d-flex justify-content-center">
				<Button
					id="btnToggle"
					className="bg-primary"
					onClick={() => {
						let i = 0;
						traversed.forEach(cell => {
							i += 0.1;

							let t = document.getElementById(`${cell}`);
							t.classList.remove("visited", "reached", "normal");
							void t.offsetWidth;
							if (cell !== `${traversed[traversed.length - 1]}`) {
								t.classList.add("visited");
								t.style.animationDelay = `${i}s`;
							} else {
								t.classList.add("reached");
								t.style.animationDelay = `${i}s`;
								alert(t.classList);
							}
							t = null;
						});
					}}
				>
					{() => {
						return play ? "stop" : "play";
					}}
				</Button>
			</Row>
		</Container>
	);
};
export default SearchView;
