import React from "react";
import { useState, useEffect } from "react";
import { Fragment } from "react";

//bootstap imports
import "./../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./../node_modules/bootstrap/dist/js/bootstrap.min.js";

//react-bootstrap imports
import { Container, Row, Col } from "react-bootstrap";

//Components imports
import MainNav from "./Components/MainNav";
import Home from "./Components/Home/Home.jsx";
import About from "./Components/About Me/About.jsx";
import Projects from "./Components/Projects/Projects.jsx";
import Contact from "./Components/Contact Me/Contact.jsx";

//App
const App = () => {
	const [view, setView] = useState("");
	useEffect(() => {
		alert(view);
	}, [view]);

	const toggleView = changeTo => {
		setView(changeTo);
	};
	return (
		<Container fluid="md" data-bs-theme="dark" style = {{margin:0}}>
			<MainNav toggle={toggleView} />
			<Container>
				{(() => {
					switch (view) {
						case "Home":
							return <Home />;
						case "About":
							return <About />;
						case "Projects":
							return <Projects />;
						case "Contact":
							return <Contact />;
						default:
							return null;
					}
				})()}
			</Container>
		</Container>
	);
};

export default App;
