import React from "react";

export const checkState = board => {
	const winningSequences = [
		[0, 3, 6],
		[1, 4, 7],
		[2, 5, 8],
		[0, 1, 2],
		[3, 4, 5],
		[6, 7, 8],
		[0, 4, 8],
		[2, 4, 6]
	];
	//Check X
	let state = "";
	winningSequences.forEach(sequence => {
		let count = 0;
		sequence.forEach(square => {
			if (board[square] === "X") {
				count++;
			}
		});
		if (count === 3) {
			state = "X";
		}
	});
	if (state === "") {
		winningSequences.forEach(sequence => {
			let count = 0;
			sequence.forEach(square => {
				if (board[square] === "O") {
					count++;
				}
			});
			if (count === 3) {
				state = "O";
			}
		});
	}
	if (state === "") {
		board.forEach(square => {
			if (square !== "O" && square !== "X") {
				state = "Live";
			}
		});
	}
	if (state === "") {
		state = "Live";
	}
	return state;
};
export const isValidMove = (board, move) => {
	if (board[move] === "X" || board[move] === "O") {
		return false;
	} else {
		return true;
	}
};
