import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";

import O from "../Resources/Icons/letter-o-svgrepo-com.svg";
import X from "../Resources/Icons/x-symbol-svgrepo-com.svg";
const { checkState, isValidMove } = require("../GameLogic/Adjudicator.jsx");
const Board = () => {
	const [board, setBoard] = useState([
		"1",
		"2",
		"3",
		"4",
		"5",
		"6",
		"7",
		"8",
		"9"
	]);
	const [turn, setTurn] = useState("X");
	const attemptMove = (e, move) => {
		if (checkState(board)[0] === "Live" && isValidMove(board, move)) {
			setBoard(
				board.map(item => {
					if (item == move + 1) {
						return turn;
					} else {
						return item;
					}
				})
			);
			e.target.classList.add(turn === "X" ? "X" : "O");

			if (checkState(board)[0] !== "Live") {
				alert(`Game Over: ${turn}`);
			}
			setTurn(turn === "X" ? "O" : "X");
		}
	};

	return (
		<Container className="d-grid Board ms-1 me-1 mt-3">
			<Row className="d-flex justify-content-center">
				<Col>
					<div
						id="one"
						className="square"
						onClick={e => {
							attemptMove(e, 0);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="two"
						className="square"
						onClick={e => {
							attemptMove(e, 1);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="three"
						className="square"
						onClick={e => {
							attemptMove(e, 2);
						}}
					></div>
				</Col>
			</Row>
			<Row className="d-flex justify-content-center">
				<Col>
					{" "}
					<div
						id="four"
						className="square"
						onClick={e => {
							attemptMove(e, 3);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="five"
						className="square"
						onClick={e => {
							attemptMove(e, 4);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="six"
						className="square"
						onClick={e => {
							attemptMove(e, 5);
						}}
					></div>
				</Col>
			</Row>
			<Row className="d-flex justify-content-center">
				<Col>
					{" "}
					<div
						id="seven"
						className="square"
						onClick={e => {
							attemptMove(e, 6);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="eight"
						className="square"
						onClick={e => {
							attemptMove(e, 7);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="nine"
						className="square"
						onClick={e => {
							attemptMove(e, 8);
						}}
					></div>
				</Col>
			</Row>
		</Container>
	);
};

export default Board;
