export const BFS = (
	tree,
	rootNode,
	destination,
	visited = [],
	queue = [rootNode]
) => {
	if (visited.indexOf(rootNode) !== -1) {
		queue.shift();
		rootNode = queue[0];
	} else {
		visited.push(rootNode);
	}

	queue.push(...tree[Number(rootNode)]);
	queue.shift();
	if (rootNode === destination) {
		return { found: true, checked: visited };
	} else if (queue.length === 0) {
		return { found: false, checked: visited };
	} else {
		return BFS(tree, queue[0], destination, visited, queue);
	}
};

export const DFS = (
	tree,
	rootNode,
	destination,
	visited = [],
	stack = [rootNode],
	stacks = []
) => {
	if (visited.indexOf(rootNode) !== -1) {
		stack.shift();
		rootNode = stack[0];
	} else {
		visited.push(rootNode);
	}
	stack.shift();
	stack.unshift(...tree[rootNode]);
	stacks.push(stack);

	if (rootNode === destination) {
		return { found: true, 
		checked: [...visited,[...stacks]], 
		st: [...stacks] };
	} else if (stack.length === 0) {
		return { found: false, checked: [...visited], st: [...stacks] };
	} else {
		return BFS(tree, stack[0], destination, visited, stack, stacks);
	}
