import React, { useState, useEffect } from "react";

import "./TTT.css";
import "./../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./../node_modules/bootstrap/dist/js/bootstrap.min.js";

import { Container, Row, Col, Button } from "react-bootstrap";
import MainNav from "./Components/MainNav.jsx";
import Board from "./Components/Board.jsx";
//App
const App = () => {
	const [mode, setMode] = useState("Multiplayer");
	const [opponent, setOpponent] = useState("Player 2");
	const [oppColor, setOppColor] = useState("#24caf0");
useEffect(() => {
  switch(mode){
    case "Multiplayer": setOpponent("Player 2");
    case "Easy": setOpponent("Siya");
    case "Medium": setOpponent("SBS");
    case "Hard": setOpponent("ZEN OTI");
  }
}, [mode])

	const toggleMode = toMode => {
		setMode(toMode);
	};
	return (
		<Container
			fluid
			className="d-flex flex-column min-vh-100 min-vw-100 bg-secondary-subtle overflow-x-hidden p-0 text-white"
			data-bs-theme="dark"
		>
			<MainNav toggleMode={toggleMode} />
			<h1 className="text-center mt-3 mb-1 NBrand">{mode}</h1>
			<Row className="text-center mb-4">
				<Col>
					<h2>Player 1</h2>
				</Col>
				<Col>
					<h2>{opponent}</h2>
				</Col>
			</Row>
			<Board />
		</Container>
	);
};

export default App;
