import React, { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { checkState, isValidMove } from "../GameLogic/Adjudicator.jsx";
import O from "../Resources/Icons/letter-o-svgrepo-com.svg";
import X from "../Resources/Icons/x-symbol-svgrepo-com.svg";
const Board = () => {
	const [board, setBoard] = useState([
		"1",
		"2",
		"3",
		"4",
		"5",
		"6",
		"7",
		"8",
		"9"
	]);
	const [turn, setTurn] = useState(false);
	const attemptMove = move => {
		if (checkState(board) === "Live" && isValidMove(board, move)) {
			setBoard(
				board.map(item => {
					if (item == move + 1) {
						return turn;
					} else {
						return item;
					}
				})
			);
			if (checkState(board) !== "Live") {
				alert(`Game Over: ${turn}`);
			}
			setTurn(!turn);
		}
	};
	return (
		<Container className="d-grid Board ms-1 me-1 mt-3">
			<Row className="d-flex justify-content-center">
				<Col>
					<div
						id="one"
						className="square"
						onClick={e => {
							attemptMove(0);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="two"
						className="square"
						onClick={e => {
							attemptMove(1);
							e.target.backgroundImage=(turn ? "url('Resources/Icons/x-symbol-svgrepo-com.svg')" :"url('Resources/Icons/letter-O-svgrepo-com.svg')" )
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="three"
						className="square"
						onClick={e => {
							attemptMove(2);
						}}
					></div>
				</Col>
			</Row>
			<Row className="d-flex justify-content-center">
				<Col>
					{" "}
					<div
						id="four"
						className="square"
						onClick={e => {
							attemptMove(3);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="five"
						className="square"
						onClick={e => {
							attemptMove(4);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="six"
						className="square"
						onClick={e => {
							attemptMove(5);
						}}
					></div>
				</Col>
			</Row>
			<Row className="d-flex justify-content-center">
				<Col>
					{" "}
					<div
						id="seven"
						className="square"
						onClick={e => {
							attemptMove(6);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="eight"
						className="square"
						onClick={e => {
							attemptMove(7);
						}}
					></div>
				</Col>
				<Col>
					{" "}
					<div
						id="nine"
						className="square"
						onClick={e => {
							attemptMove(8);
						}}
					></div>
				</Col>
			</Row>
		</Container>
	);
};

export default Board;
